import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
  root: {
    minWidth: 275,
    backgroundColor: "#456e82"
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  list: {
    listStyleType: "none",
    textAlign: "left"
  },
  main: {
    textAlign: 'left',
    color: "black"
  }
});

export default function CVCard(props) {
  const classes = useStyles();
  const bull = <span className={classes.bullet}>â€¢</span>;
  const listItems = props.bullets.map(bullet =>
    <li key={bullet.num} style={{ paddingBottom: "5px" }}> {bullet.descript} </li>
  )

  return (
    <Card className={classes.root} variant="outlined">
      <CardContent>
        <Typography variant="h6" component="h6" className={classes.main}>
          {bull}{props.main}
        </Typography>
        <Typography className={classes.pos} color="textSecondary">
          {props.time}
        </Typography>
        <Typography variant="body2" component="p">
          <ul className={classes.list}>
            {listItems}
          </ul>
        </Typography>
      </CardContent>
    </Card>
  );
}
